def twoTwo(a):  
    def findlentwo():
        dmpln1 = []
        for i in range(0,801):
            ln = len(str(pow(2,i)))
            if ln not in dmpln1:
                dmpln1.append(ln)
        return dmpln1

    dmpln1 = findlentwo()
    num = 0
    l = len(a)+1
    
    for i in dmpln1: #range(1,l):
        for j in range(0,(l-i)):
            ans = int(a[j:j+i])
            if (int(a[j]) > 0 ): 
                if (ans & (ans - 1)):
                    continue
                else:
                    num += 1
                    
    return int(num)

